--
-- Name: unaccent_string(text, numeric); Type: FUNCTION; Schema: adempiere; Owner: adempiere
--

CREATE FUNCTION adempiere.unaccent_string(text, numeric) RETURNS text
    LANGUAGE plpgsql
    AS $_$
DECLARE
    input_string text := $1;
    output_string text;
    version numeric := $2;
BEGIN

input_string := translate(input_string, 'aaaaaAAAAAA', 'aaaaaaaaaaaaaa');
input_string := translate(input_string, 'eeeeeeeEEEE', 'eeeeeeeeeeeeeee');
input_string := translate(input_string, 'iiiiiIIIII', 'iiiiiiiiiiiiiiii');
input_string := translate(input_string, 'ooooOOOOOO', 'oooooooooooooo');
input_string := translate(input_string, 'uuuuuuUUUUUUU', 'uuuuuuuuuuuuuuu');
input_string := translate(input_string, '', 'c');
if version = 1 then
 input_string := replace(lower(input_string),'','ss');
 input_string := replace(lower(input_string),'','ae');
 input_string := replace(lower(input_string),'','oe');
 input_string := replace(lower(input_string),'','ue');
else
 input_string := replace(lower(input_string),'ss','');
 input_string := replace(lower(input_string),'ae','');
 input_string := replace(lower(input_string),'oe','');
 input_string := replace(lower(input_string),'ue','');
end if;
return input_string;
END;
$_$;


