        END * (l.qtyordered - sum(COALESCE(m.qty, (0)::numeric)))) AS qtyentered,
    l.c_uom_id,
    (l.qtyordered - sum(COALESCE(m.qty, (0)::numeric))) AS movementqty,
        CASE
